sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function(strDate) {
		// Create GlideDateTime objects for the current date and the past date
		this.rightnow = new GlideDateTime();
		this.testdate = new GlideDateTime(strDate);
    },
	isDatePast: function() {
		// If the testdate is before rightnow, return true, else return false
		if(this.testdate.before(this.rightnow)) {
			return true;
		} else {
			return false;
		}
	},
	isDayToday: function() {
		// If testdate is equal to rightnow return true, else return false
		if(this.testdate.getDate() == this.rightnow.getDate()) {
			return true;
		} else {
			return false;
		}
	},

    type: 'NeedItUtils'
};
